import React,{useState} from "react";

function CreateArea() {
const [note, setNote]  = useState({
  title: "",
  content:""
});


function handleChange(event){
const {name, value} = event.target;

setNote (prevNote => {
  return {
    ...prevNote,
    [name]:value
  };
})

  //   if (name==="title") {
  //   setNote((prevNote) => {return {title: value, note:prevNote}});
  // } else {
  //   setNote((prevTitle) => { return { title: prevTitle, note: value } })

  return (
    <div>
      <form>
        <note onChange={handleChange} name="title" placeholder="Title" value={note.title}/>
        <textarea onChange={handleChange} name="content" placeholder="Take a note..." rows="3" value={note.content}/>
        <button>Add</button>
      </form>
    </div>
  );
}
}

export default CreateArea;
