import React from "react";

function Note(props) {

function handleClickDel() {
    props.onDelete(props.id)
}

  return (
    <div className="note">
      <h1>{props.title}</h1>
      <p>{props.content}</p>
      <button onClick={handleClickDel}>DELETE</button>
    </div>
  );
}

export default Note;
